// App.js
import React from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles/bootstrap-custom.css';

import { AuthProvider } from './context/AuthContext';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import Products from './pages/Products';
import PurchaseOrders from './pages/PurchaseOrders';
import SupplierList from './pages/SupplierList';
import StockManagement from './pages/StockManagement';
import UserManagement from './pages/UserManagement';
import ProtectedRoute from './components/ProtectedRoute';

function App() {
  return (
    <AuthProvider>
      <Router>
        <Switch>
          <Route path="/login" component={Login} />
          <Route path="/unauthorized" render={() => <div>Unauthorized Access</div>} />

          <ProtectedRoute exact path="/" component={Dashboard} allowedRoles={['User', 'Admin', 'SuperAdmin']} />
          <ProtectedRoute path="/products" component={Products} allowedRoles={['Admin', 'SuperAdmin']} />
          <ProtectedRoute path="/orders" component={PurchaseOrders} allowedRoles={['Admin', 'SuperAdmin']} />
          <ProtectedRoute path="/suppliers" component={SupplierList} allowedRoles={['Admin', 'SuperAdmin']} />
          <ProtectedRoute path="/stock" component={StockManagement} allowedRoles={['Admin', 'SuperAdmin']} />
          <ProtectedRoute path="/users" component={UserManagement} allowedRoles={['SuperAdmin']} />
        </Switch>
      </Router>
    </AuthProvider>
  );
}

export default App;
