import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';
import { Package, Users, ShoppingCart, AlertTriangle, Home, Box, ShoppingBag, Truck, BarChart3, User } from 'lucide-react';

const Dashboard = () => {
  const [summaryData, setSummaryData] = useState({
    totalProducts: 0,
    totalSuppliers: 0,
    pendingOrders: 0,
    stockAlerts: 0
  });
  const [products, setProducts] = useState([]);
  const [categoryData, setCategoryData] = useState([]);
  const [stockLevels, setStockLevels] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // API base URL - adjust if your backend runs on different port
  const API_BASE = 'http://localhost:5000/api/dah';

  // Fetch all dashboard data
  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      
      // Fetch summary data
      const summaryResponse = await fetch(`${API_BASE}/dashboard/summary`);
      if (summaryResponse.ok) {
        const summary = await summaryResponse.json();
        setSummaryData(summary);
      }

      // Fetch products
      const productsResponse = await fetch(`${API_BASE}/dashboard/products`);
      if (productsResponse.ok) {
        const productsData = await productsResponse.json();
        setProducts(productsData);
      }

      // Fetch category distribution
      const categoryResponse = await fetch(`${API_BASE}/dashboard/category-distribution`);
      if (categoryResponse.ok) {
        const categoryDistribution = await categoryResponse.json();
        setCategoryData(categoryDistribution);
      }

      // Fetch stock levels
      const stockResponse = await fetch(`${API_BASE}/dashboard/stock-levels`);
      if (stockResponse.ok) {
        const stockData = await stockResponse.json();
        setStockLevels(stockData);
      }

    } catch (err) {
      setError('Failed to fetch dashboard data');
      console.error('Dashboard error:', err);
    } finally {
      setLoading(false);
    }
  };

  // Colors for pie chart
  const COLORS = ['#10B981', '#F59E0B', '#EF4444', '#3B82F6', '#8B5CF6'];

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-emerald-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <AlertTriangle className="h-16 w-16 text-red-500 mx-auto mb-4" />
          <p className="text-red-600">{error}</p>
          <button 
            onClick={fetchDashboardData}
            className="mt-4 px-4 py-2 bg-emerald-600 text-white rounded hover:bg-emerald-700"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Sidebar */}
      <div className="fixed left-0 top-0 h-full w-72 bg-emerald-600 text-white">
        <div className="p-6">
          <h1 className="text-2xl font-bold">MarketTrack</h1>
        </div>
        
        <nav className="mt-8">
          <div className="px-6 py-3 bg-emerald-700 flex items-center space-x-3">
            <Home className="h-5 w-5" />
            <span className="font-medium">Dashboard</span>
          </div>
          <div className="px-6 py-3 flex items-center space-x-3 hover:bg-emerald-700 cursor-pointer">
            <Box className="h-5 w-5" />
            <span>Product Management</span>
          </div>
          <div className="px-6 py-3 flex items-center space-x-3 hover:bg-emerald-700 cursor-pointer">
            <ShoppingBag className="h-5 w-5" />
            <span>Purchase Order</span>
          </div>
          <div className="px-6 py-3 flex items-center space-x-3 hover:bg-emerald-700 cursor-pointer">
            <Truck className="h-5 w-5" />
            <span>Suppliers</span>
          </div>
          <div className="px-6 py-3 flex items-center space-x-3 hover:bg-emerald-700 cursor-pointer">
            <BarChart3 className="h-5 w-5" />
            <span>Stock Management</span>
          </div>
          <div className="px-6 py-3 flex items-center space-x-3 hover:bg-emerald-700 cursor-pointer">
            <User className="h-5 w-5" />
            <span>Account Management</span>
          </div>
        </nav>

        <div className="absolute bottom-4 left-6 flex items-center space-x-2">
          <User className="h-5 w-5" />
          <span className="text-sm">user</span>
        </div>
      </div>

      {/* Main Content */}
      <div className="ml-72 p-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">Welcome back, Admin</h1>
          <h2 className="text-xl text-gray-600 mt-2">Dashboard</h2>
        </div>

        {/* Summary Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Total Products</p>
                <p className="text-3xl font-bold text-gray-900">{summaryData.totalProducts}</p>
              </div>
              <Package className="h-12 w-12 text-emerald-600" />
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Total Suppliers</p>
                <p className="text-3xl font-bold text-gray-900">{summaryData.totalSuppliers}</p>
              </div>
              <Users className="h-12 w-12 text-blue-600" />
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Pending Orders</p>
                <p className="text-3xl font-bold text-gray-900">{summaryData.pendingOrders}</p>
              </div>
              <ShoppingCart className="h-12 w-12 text-orange-600" />
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Stock Alert</p>
                <p className="text-3xl font-bold text-gray-900">{summaryData.stockAlerts}</p>
              </div>
              <AlertTriangle className="h-12 w-12 text-red-600" />
            </div>
          </div>
        </div>

        {/* Content Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Products Table */}
          <div className="lg:col-span-2 bg-white rounded-lg shadow">
            <div className="p-6">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-lg font-semibold text-gray-900">Recent Products</h3>
                <button 
                  onClick={fetchDashboardData}
                  className="text-emerald-600 hover:text-emerald-700 text-sm font-medium"
                >
                  Refresh
                </button>
              </div>
              
              <div className="overflow-x-auto">
                <table className="min-w-full">
                  <thead>
                    <tr className="border-b">
                      <th className="text-left py-3 px-4 font-medium text-gray-700">Category</th>
                      <th className="text-left py-3 px-4 font-medium text-gray-700">Product</th>
                      <th className="text-left py-3 px-4 font-medium text-gray-700">ID</th>
                      <th className="text-left py-3 px-4 font-medium text-gray-700">In Stock</th>
                    </tr>
                  </thead>
                  <tbody>
                    {products.map((product, index) => (
                      <tr key={product.product_ID} className="border-b hover:bg-gray-50">
                        <td className="py-3 px-4 text-gray-600">{product.category_Name || 'Uncategorized'}</td>
                        <td className="py-3 px-4 text-gray-900">{product.product_Name}</td>
                        <td className="py-3 px-4 text-gray-600">{product.product_ID}</td>
                        <td className="py-3 px-4">
                          <span className={`px-2 py-1 rounded text-sm ${
                            product.stock_Quantity < 10 
                              ? 'bg-red-100 text-red-800' 
                              : 'bg-green-100 text-green-800'
                          }`}>
                            {product.stock_Quantity}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>

          {/* Stock Levels Chart */}
          <div className="bg-white rounded-lg shadow p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Stock Levels</h3>
            <ResponsiveContainer width="100%" height={200}>
              <BarChart data={stockLevels}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis 
                  dataKey="category_Name" 
                  tick={{ fontSize: 12 }}
                  angle={-45}
                  textAnchor="end"
                  height={80}
                />
                <YAxis />
                <Tooltip />
                <Bar dataKey="total_stock" fill="#10B981" />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Pie Chart */}
        {categoryData.length > 0 && (
          <div className="mt-8 bg-white rounded-lg shadow p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Category Distribution</h3>
            <div className="flex justify-center">
              <ResponsiveContainer width={400} height={300}>
                <PieChart>
                  <Pie
                    data={categoryData}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    label={({ category_Name, percentage }) => `${category_Name}: ${percentage}%`}
                    outerRadius={80}
                    fill="#8884d8"
                    dataKey="product_count"
                  >
                    {categoryData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip />
                </PieChart>
              </ResponsiveContainer>
            </div>
            <div className="flex flex-wrap justify-center mt-4 space-x-4">
              {categoryData.map((entry, index) => (
                <div key={entry.category_Name} className="flex items-center space-x-2">
                  <div 
                    className="w-3 h-3 rounded-full" 
                    style={{ backgroundColor: COLORS[index % COLORS.length] }}
                  ></div>
                  <span className="text-sm text-gray-600">{entry.category_Name}</span>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
